<style lang="scss">
  .icon-svg {
    path {
      fill: #fff;
    }

    &.play {
      circle {
        fill: #000;
        stroke: #fff;
        opacity: 0.4;
      }

      path {
        fill: #fff;
        stroke: #fff;
      }
    }

    &.imu {
      svg {
        width: 1.1rem;
        height: auto;
      }

      path {
        fill: #fff;
      }
    }

    &.facebook {
      height: 2.25rem;

      svg {
        width: 1rem;
      }
    }

    &.linkedin {
      height: 2.25rem;

      svg {
        width: 2rem;
      }
    }

    &.soundcloud {
      height: 2.4rem;

      svg {
        width: 2.4rem;
      }
    }
  }

  .thumb-button {
    .thumb {
      width: 1.4rem;
      height: 1.6rem;
    }

    &:hover, &.voted_up {
      path {
        fill: #e41c69;
      }
    }
  }

  .sidebar {
    path {
      fill: #9f9f9f;
    }

    a:hover {
      path {
        fill: #fff;
      }
    }
    a.router-link-exact-active {
      path {
        fill: #fff;
      }
    }
  }

  a.prev,
  a.next {
    display: block;
    margin: 0 1rem;

    &:hover {
      svg path {
        fill: #e41c69;
      }
    }
  }

  .spinner {
    background: linear-gradient(180deg, #000, #ff4395, #9b19bb, #000);
    background-size: 800% 800%;
    animation: Spinner 1s ease-in-out infinite;
    border-radius: 80rem;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  @keyframes Spinner {
    0% {
      background-position: 50% 0%;
    }
    50% {
      background-position: 51% 100%;
    }
    100% {
      background-position: 50% 0%;
    }
  }
</style>
